insert into direito (id, id_nivel, codigo, ativo, bncc, descricao) values (1, 1, 'D0001', true, true, 'CONVIVER com outras crianças e adultos, em pequenos e grandes grupos, utilizando diferentes linguagens, ampliando o conhecimento de si e do outro, o respeito em relação à cultura e às diferenças entre as pessoas');
insert into direito (id, id_nivel, codigo, ativo, bncc, descricao) values (2, 1, 'D0002', true, true, 'BRINCAR cotidianamente de diversas formas, em diferentes espaços e tempos, com diferentes parceiros (crianças e adultos), ampliando e diversificando seu acesso a produções culturais, seus conhecimentos, sua imaginação, sua criatividade, suas experiências emocionais, corporais, sensoriais, expressivas, cognitivas, sociais e relacionais');
insert into direito (id, id_nivel, codigo, ativo, bncc, descricao) values (3, 1, 'D0003', true, true, 'PARTICIPAR ativamente, com adultos e outras crianças, tanto do planejamento da gestão da escola e das atividades propostas pelo educador quanto da realização das atividades da vida cotidiana, tais como a escolha das brincadeiras, dos materiais e dos ambientes, desenvolvendo diferentes linguagens e elaborando conhecimentos, decidindo e se posicionando');
insert into direito (id, id_nivel, codigo, ativo, bncc, descricao) values (4, 1, 'D0004', true, true, 'EXPLORAR movimentos, gestos, sons, formas, texturas, cores, palavras, emoções, transformações, relacionamentos, histórias, objetos, elementos da natureza, na escola e fora dela, ampliando seus saberes sobre a cultura, em suas diversas modalidades: as artes, a escrita, a ciência e a tecnologia');
insert into direito (id, id_nivel, codigo, ativo, bncc, descricao) values (5, 1, 'D0005', true, true, 'EXPRESSAR, como sujeito dialógico, criativo e sensível, suas necessidades, emoções, sentimentos, dúvidas, hipóteses, descobertas, opiniões, questionamentos, por meio de diferentes linguagens');
insert into direito (id, id_nivel, codigo, ativo, bncc, descricao) values (6, 1, 'D0006', true, true, 'CONHECER-SE e CONSTRUIR sua identidade pessoal, social e cultural, constituindo uma imagem positiva de si e de seus grupos de pertencimento, nas diversas experiências de cuidados, interações, brincadeiras e linguagens vivenciadas na instituição escolar e em seu contexto familiar e comunitário');
