insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (1, 'Valorizar e utilizar os conhecimentos historicamente construídos sobre o mundo físico, social, cultural e digital para entender e explicar a realidade, continuar aprendendo e colaborar para a construção de uma sociedade justa, democrática e inclusiva', 2, Null, 'C0001', true, true, true);
