insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (3, 'Valorizar e fruir as diversas manifestações artísticas e culturais, das locais às mundiais, e também participar de práticas diversificadas da produção artístico-cultural', 2, Null, 'C0003', true, true, true);
