insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (6, 'Valorizar a diversidade de saberes e vivências culturais e apropriar-se de conhecimentos e experiências que lhe possibilitem entender as relações próprias do mundo do trabalho e fazer escolhas alinhadas ao exercício da cidadania e ao seu projeto de vida, com liberdade, autonomia, consciência crítica e responsabilidade', 2, Null, 'C0006', true, true, true);
