insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (11, 'Compreender a si e ao outro como identidades diferentes, de forma a exercitar o respeito à diferença em uma sociedade plural e promover os direitos humanos', 2, 1, 'C0011', true, false, true);
