insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (392, 7, 'EF15AR01', true, true, 'Identificar e apreciar formas distintas das artes visuais tradicionais e contemporâneas, cultivando a percepção, o imaginário, a capacidade de simbolizar e o repertório imagético', Null, Null, 'Contextos e práticas', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (393, 7, 'EF15AR02', true, true, 'Explorar e reconhecer elementos constitutivos das artes visuais (ponto, linha, forma, cor, espaço, movimento etc)', Null, Null, 'Elementos da linguagem', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (394, 7, 'EF15AR03', true, true, 'Reconhecer e analisar a influência de distintas matrizes estéticas e culturais das artes visuais nas manifestações artísticas das culturas locais, regionais e nacionais', Null, Null, 'Matrizes estéticas e culturais', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (395, 7, 'EF15AR04', true, true, 'Experimentar diferentes formas de expressão artística (desenho, pintura, colagem, quadrinhos, dobradura, escultura, modelagem, instalação, vídeo, fotografia etc), fazendo uso sustentável de materiais, instrumentos, recursos e técnicas convencionais e não convencionais', Null, Null, 'Materialidades', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (396, 7, 'EF15AR05', true, true, 'Experimentar a criação em artes visuais de modo individual, coletivo e colaborativo, explorando diferentes espaços da escola e da comunidade', Null, Null, 'Processos de criação', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (397, 7, 'EF15AR06', true, true, 'Dialogar sobre a sua criação e as dos colegas, para alcançar sentidos plurais', Null, Null, 'Processos de criação', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (398, 7, 'EF15AR07', true, true, 'Reconhecer algumas categorias do sistema das artes visuais (museus, galerias, instituições, artistas, artesãos, curadores etc)', Null, Null, 'Sistemas da linguagem', 'Artes visuais');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (399, 7, 'EF15AR08', true, true, 'Experimentar e apreciar formas distintas de manifestações da dança presentes em diferentes contextos, cultivando a percepção, o imaginário, a capacidade de simbolizar e o repertório corporal', Null, Null, 'Contextos e práticas', 'Dança');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (400, 7, 'EF15AR09', true, true, 'Estabelecer relações entre as partes do corpo e destas com o todo corporal na construção do movimento dançado', Null, Null, 'Elementos da linguagem', 'Dança');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (401, 7, 'EF15AR10', true, true, 'Experimentar diferentes formas de orientação no espaço (deslocamentos, planos, direções, caminhos etc) e ritmos de movimento (lento, moderado e rápido) na construção do movimento dançado', Null, Null, 'Elementos da linguagem', 'Dança');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (402, 7, 'EF15AR11', true, true, 'Criar e improvisar movimentos dançados de modo individual, coletivo e colaborativo, considerando os aspectos estruturais, dinâmicos e expressivos dos elementos constitutivos do movimento, com base nos códigos de dança', Null, Null, 'Processos de criação', 'Dança');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (403, 7, 'EF15AR12', true, true, 'Discutir, com respeito e sem preconceito, as experiências pessoais e coletivas em dança vivenciadas na escola, como fonte para a construção de vocabulários e repertórios próprios', Null, Null, 'Processos de criação', 'Dança');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (404, 7, 'EF15AR13', true, true, 'Identificar e apreciar criticamente diversas formas e gêneros de expressão musical, reconhecendo e analisando os usos e as funções da música em diversos contextos de circulação, em especial, aqueles da vida cotidiana', Null, Null, 'Contextos e práticas', 'Música');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (405, 7, 'EF15AR14', true, true, 'Perceber e explorar os elementos constitutivos da música (altura, intensidade, timbre, melodia, ritmo etc), por meio de jogos, brincadeiras, canções e práticas diversas de composição/criação, execução e apreciação musical', Null, Null, 'Elementos da linguagem', 'Música');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (406, 7, 'EF15AR15', true, true, 'Explorar fontes sonoras diversas, como as existentes no próprio corpo (palmas, voz, percussão corporal), na natureza e em objetos cotidianos, reconhecendo os elementos constitutivos da música e as características de instrumentos musicais variados', Null, Null, 'Materialidades', 'Música');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (407, 7, 'EF15AR16', true, true, 'Explorar diferentes formas de registro musical não convencional (representação gráfica de sons, partituras criativas etc), bem como procedimentos e técnicas de registro em áudio e audiovisual, e reconhecer a notação musical convencional', Null, Null, 'Notação e registro musical', 'Música');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (408, 7, 'EF15AR17', true, true, 'Experimentar improvisações, composições e sonorização de histórias, entre outros, utilizando vozes, sons corporais e/ou instrumentos musicais convencionais ou não convencionais, de modo individual, coletivo e colaborativo', Null, Null, 'Processos de criação', 'Música');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (409, 7, 'EF15AR18', true, true, 'Reconhecer e apreciar formas distintas de manifestações do teatro presentes em diferentes contextos, aprendendo a ver e a ouvir histórias dramatizadas e cultivando a percepção, o imaginário, a capacidade de simbolizar e o repertório ficcional', Null, Null, 'Contextos e práticas', 'Teatro');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (410, 7, 'EF15AR19', true, true, 'Descobrir teatralidades na vida cotidiana, identificando elementos teatrais (variadas entonações de voz, diferentes fisicalidades, diversidade de personagens e narrativas etc)', Null, Null, 'Elementos da linguagem', 'Teatro');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (411, 7, 'EF15AR20', true, true, 'Experimentar o trabalho colaborativo, coletivo e autoral em improvisações teatrais e processos narrativos criativos em teatro, explorando desde a teatralidade dos gestos e das ações do cotidiano até elementos de diferentes matrizes estéticas e culturais', Null, Null, 'Processos de criação', 'Teatro');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (412, 7, 'EF15AR21', true, true, 'Exercitar a imitação e o faz de conta, ressignificando objetos e fatos e experimentando-se no lugar do outro, ao compor e encenar acontecimentos cênicos, por meio de músicas, imagens, textos ou outros pontos de partida, de forma intencional e reflexiva', Null, Null, 'Processos de criação', 'Teatro');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (413, 7, 'EF15AR22', true, true, 'Experimentar possibilidades criativas de movimento e de voz na criação de um personagem teatral, discutindo estereótipos', Null, Null, 'Processos de criação', 'Teatro');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (414, 7, 'EF15AR23', true, true, 'Reconhecer e experimentar, em projetos temáticos, as relações processuais entre diversas linguagens artísticas', Null, Null, 'Processos de criação', 'Artes integradas');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (415, 7, 'EF15AR24', true, true, 'Caracterizar e experimentar brinquedos, brincadeiras, jogos, danças, canções e histórias de diferentes matrizes estéticas e culturais', Null, Null, 'Matrizes estéticas e culturais', 'Artes integradas');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (416, 7, 'EF15AR25', true, true, 'Conhecer e valorizar o patrimônio cultural, material e imaterial, de culturas diversas, em especial a brasileira, incluindo-se suas matrizes indígenas, africanas e europeias, de diferentes épocas, favorecendo a construção de vocabulário e repertório relativos às diferentes linguagens artísticas', Null, Null, 'Patrimônio cultural', 'Artes integradas');
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (417, 7, 'EF15AR26', true, true, 'Explorar diferentes tecnologias e recursos digitais (multimeios, animações, jogos eletrônicos, gravações em áudio e vídeo, fotografia, softwares etc) nos processos de criação artística', Null, Null, 'Arte e tecnologia', 'Artes integradas');
