insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (454, 8, 'EF12EF02', true, true, 'Explicar, por meio de múltiplas linguagens (corporal, visual, oral e escrita), as brincadeiras e os jogos populares do contexto comunitário e regional, reconhecendo e valorizando a importância desses jogos e brincadeiras para suas culturas de origem', Null, Null, 'Brincadeiras e jogos da cultura popular presentes no contexto comunitário e regional', 'Brincadeiras e jogos');
