insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (456, 8, 'EF12EF04', true, true, 'Colaborar na proposição e na produção de alternativas para a prática, em outros momentos e espaços, de brincadeiras e jogos e demais práticas corporais tematizadas na escola, produzindo textos (orais, escritos, audiovisuais) para divulgá-las na escola e na comunidade', Null, Null, 'Brincadeiras e jogos da cultura popular presentes no contexto comunitário e regional', 'Brincadeiras e jogos');
