insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (95, 'Valorizar e utilizar os conhecimentos historicamente construídos sobre o mundo físico, social, cultural e digital para entender e explicar a realidade, continuar aprendendo e colaborar para a construção de uma sociedade justa, democrática e inclusiva.', 3, Null, 'C0001', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (96, 'Exercitar a curiosidade intelectual e recorrer à abordagem própria das ciências, incluindo a investigação, a reflexão, a análise crítica, a imaginação e a criatividade, para investigar causas, elaborar e testar hipóteses, formular e resolver problemas e criar soluções (inclusive tecnológicas) com base nos conhecimentos das diferentes áreas.', 3, Null, 'C0002', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (97, 'Valorizar e fruir as diversas manifestações artísticas e culturais, das locais às mundiais, e também participar de práticas diversificadas da produção artístico-cultural.', 3, Null, 'C0003', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (98, 'Utilizar diferentes linguagens – verbal (oral ou visual-motora, como Libras, e escrita), corporal, visual, sonora e digital –, bem como conhecimentos das linguagens artística, matemática e científica, para se expressar e partilhar informações, experiências, ideias e sentimentos em diferentes contextos e produzir sentidos que levem ao entendimento mútuo.', 3, Null, 'C0004', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (99, 'Compreender, utilizar e criar tecnologias digitais de informação e comunicação de forma crítica, significativa, reflexiva e ética nas diversas práticas sociais (incluindo as escolares) para se comunicar, acessar e disseminar informações, produzir conhecimentos, resolver problemas e exercer protagonismo e autoria na vida pessoal e coletiva.', 3, Null, 'C0005', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (100, 'Valorizar a diversidade de saberes e vivências culturais e apropriar-se de conhecimentos e experiências que lhe possibilitem entender as relações próprias do mundo do trabalho e fazer escolhas alinhadas ao exercício da cidadania e ao seu projeto de vida, com liberdade, autonomia, consciência crítica e responsabilidade.', 3, Null, 'C0006', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (101, 'Argumentar com base em fatos, dados e informações confiáveis, para formular, negociar e defender ideias, pontos de vista e decisões comuns que respeitem e promovam os direitos humanos, a consciência socioambiental e o consumo responsável em âmbito local, regional e global, com posicionamento ético em relação ao cuidado de si mesmo, dos outros e do planeta.', 3, Null, 'C0007', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (102, 'Conhecer-se, apreciar-se e cuidar de sua saúde física e emocional, compreendendo-se na diversidade humana e reconhecendo suas emoções e as dos outros, com autocrítica e capacidade para lidar com elas.', 3, Null, 'C0008', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (103, 'Exercitar a empatia, o diálogo, a resolução de conflitos e a cooperação,fazendo-se respeitar e promovendo o respeito ao outro e aos direitos humanos, com acolhimento e valorização da diversidade de indivíduos e de grupos sociais, seus saberes, identidades, culturas e potencialidades, sem preconceitos de qualquer natureza.', 3, Null, 'C0009', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (104, 'Agir pessoal e coletivamente com autonomia, responsabilidade, flexibilidade, resiliência e determinação, tomando decisões com base em princípios éticos, democráticos, inclusivos, sustentáveis e solidários.', 3, Null, 'C0010', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (105, 'Compreender o funcionamento das diferentes linguagens e práticas culturais (artísticas, corporais e verbais) e mobilizar esses conhecimentos na recepção e produção de discursos nos diferentes campos de atuação social e nas diversas mídias, para ampliar as formas de participação social, o entendimento e as possibilidades de explicação e interpretação crítica da realidade e para continuar aprendendo.', 3, 12, 'C0011', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (106, 'Compreender os processos identitários, conflitos e relações de poder que permeiam as práticas sociais de linguagem, respeitando as diversidades e a pluralidade de ideias e posições, e atuar socialmente com base em princípios e valores assentados na democracia, na igualdade e nos Direitos Humanos, exercitando o autoconhecimento, a empatia, o diálogo, a resolução de conflitos e a cooperação, e combatendo preconceitos de qualquer natureza.', 3, 12, 'C0012', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (107, 'Utilizar diferentes linguagens (artísticas, corporais e verbais) para exercer, com autonomia e colaboração, protagonismo e autoria na vida pessoal e coletiva, de forma crítica, criativa, ética e solidária, defendendo pontos de vista que respeitem o outro e promovam os Direitos Humanos, a consciência socioambiental e o consumo responsável, em âmbito local, regional e global.', 3, 12, 'C0013', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (108, 'Compreender as línguas como fenômeno (geo)político, histórico, cultural, social, variável, heterogêneo e sensível aos contextos de uso, reconhecendo suas variedades e vivenciando-as como formas de expressões identitárias, pessoais e coletivas, bem como agindo no enfrentamento de preconceitos de qualquer natureza.', 3, 12, 'C0014', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (109, 'Compreender  os  processos  de  produção  e  negociação  de  sentidos  nas  práticas  corporais, reconhecendo-as e vivenciando-as como formas de expressão de valores e identidades, em uma perspectiva democrática e de respeito à diversidade.', 3, 12, 'C0015', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (110, 'Apreciar  esteticamente  as  mais  diversas  produções  artísticas  e  culturais,  considerando  suas características  locais,  regionais  e  globais,  e  mobilizar  seus  conhecimentos  sobre  as  linguagens artísticas para dar significado e (re)construir produções autorais individuais e coletivas, exercendo protagonismo de maneira crítica e criativa, com respeito à diversidade de saberes, identidades e culturas.', 3, 12, 'C0016', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (111, 'Mobilizar práticas de linguagem no universo digital, considerando as dimensões técnicas, críticas, criativas, éticas e estéticas, para expandir as formas de produzir sentidos, de engajar-se em práticas autorais e coletivas, e de aprender a aprender nos campos da ciência, cultura, trabalho, informação e vida pessoal e coletiva.', 3, 12, 'C0017', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (112, 'Utilizar estratégias, conceitos e procedimentos matemáticos para interpretar situações em diversos contextos, sejam atividades cotidianas, sejam fatos das Ciências da Natureza e Humanas, das questões socioeconômicas ou tecnológicas, divulgados por diferentes meios, de modo a contribuir para uma formação geral.', 3, 13, 'C0018', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (113, 'Propor ou participar de ações para investigar desafios do mundo contemporâneo e tomar decisões éticas e socialmente responsáveis, com base na análise de problemas sociais, como os voltados a situações  de  saúde,  sustentabilidade,  das  implicações  da  tecnologia  no  mundo  do  trabalho,  entre outros, mobilizando e articulando conceitos, procedimentos e linguagens próprios da Matemática.', 3, 13, 'C0019', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (114, 'Utilizar  estratégias,  conceitos,  definições  e  procedimentos  matemáticos  para  interpretar,  construir modelos e resolver problemas em diversos contextos, analisando a plausibilidade dos resultados e a adequação das soluções propostas, de modo a construir argumentação consistente.', 3, 13, 'C0020', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (115, 'Compreender e utilizar, com flexibilidade e precisão, diferentes registros de representação matemáticos (algébrico,  geométrico,  estatístico,  computacional  etc.),  na  busca  de  solução  e  comunicação  de resultados de problemas.', 3, 13, 'C0021', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (116, 'Investigar e estabelecer conjecturas a respeito de diferentes conceitos e propriedades matemáticas, empregando  estratégias  e  recursos,  como  observação  de  padrões,  experimentações  e  diferentes tecnologias,  identificando  a  necessidade,  ou  não,  de  uma  demonstração  cada  vez  mais  formal  na validação das referidas conjecturas.', 3, 13, 'C0022', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (117, 'Analisar  fenômenos  naturais  e  processos  tecnológicos,  com  base  nas  interações  e  relações  entre matéria e energia, para propor ações individuais e coletivas que aperfeiçoem processos produtivos, minimizem impactos socioambientais e melhorem as condições de vida em âmbito local, regional e global.', 3, 14, 'C0023', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (118, 'Analisar  e  utilizar  interpretações  sobre  a  dinâmica  da  Vida,  da  Terra  e  do  Cosmos  para  elaborar argumentos, realizar previsões sobre o funcionamento e a evolução dos seres vivos e do Universo, e fundamentar e defender decisões éticas e responsáveis.', 3, 14, 'C0024', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (119, 'Investigar  situações-problema  e  avaliar  aplicações  do  conhecimento  científico  e  tecnológico  e  suas implicações no mundo, utilizando procedimentos e linguagens  próprios  das  Ciências  da  Natureza, para propor soluções que considerem demandas locais, regionais e/ou globais, e comunicar suas descobertas e conclusões a públicos variados, em diversos contextos e por meio de diferentes mídias e tecnologias digitais de informação e comunicação (TDIC).', 3, 14, 'C0025', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (120, 'Analisar processos políticos, econômicos, sociais, ambientais e culturais nos âmbitos local, regional, nacional e mundial em diferentes tempos, a partir da pluralidade de procedimentos epistemológicos, científicos e tecnológicos, de modo a compreender e posicionar-se criticamente em relação a eles, considerando diferentes pontos de vista e tomando decisões baseadas em argumentos e fontes de natureza científica.', 3, 15, 'C0026', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (121, 'Analisar a formação de territórios e fronteiras em diferentes tempos e espaços, mediante a compreensão das relações de poder que determinam as territorialidades e o papel geopolítico dos Estados-nações.', 3, 15, 'C0027', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (122, 'Analisar e avaliar criticamente as relações de diferentes grupos, povos e sociedades com a natureza (produção,  distribuição  e  consumo)  e  seus  impactos  econômicos  e  socioambientais,  com  vistas  à proposição  de  alternativas  que  respeitem  e  promovam  a  consciência,  a  ética  socioambiental  e  o consumo responsável em âmbito local, regional, nacional e global.', 3, 15, 'C0028', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (123, 'Analisar as relações de produção, capital e trabalho em diferentes territórios, contextos e culturas, discutindo o papel dessas relações na construção, consolidação e transformação das sociedades.', 3, 15, 'C0029', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (124, 'Identificar e combater as diversas formas de injustiça, preconceito e violência, adotando princípios éticos, democráticos, inclusivos e solidários, e respeitando os Direitos Humanos.', 3, 15, 'C0030', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (125, 'Participar  do  debate  público  de  forma  crítica,  respeitando  diferentes  posições  e  fazendo  escolhas alinhadas ao exercício da cidadania e ao seu projeto de vida, com liberdade, autonomia, consciência crítica e responsabilidade.', 3, 15, 'C0031', true, false, true);
