insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (369, 6, 'EF89LP27', true, true, 'Tecer considerações e formular problematizações pertinentes, em momentos oportunos, em situações de aulas, apresentação oral, seminário etc', 'Campo de atuação na vida pública', 'Oralidade', 'Conversação espontânea',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (370, 6, 'EF89LP28', true, true, 'Tomar nota de videoaulas, aulas digitais, apresentações multimídias, vídeos de divulgação científica, documentários e afins, identificando, em função dos objetivos, informações principais para apoio ao estudo e realizando, quando necessário, uma síntese final que destaque e reorganize os pontos ou conceitos centrais e suas relações e que, em alguns casos, seja acompanhada de reflexões pessoais, que podem conter dúvidas, questionamentos, considerações etc', 'Campo de atuação na vida pública', 'Oralidade', 'Procedimentos de apoio à compreensão Tomada de nota',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (371, 6, 'EF89LP29', true, true, 'Utilizar e perceber mecanismos de progressão temática, tais como retomadas anafóricas (“que, cujo, onde”, pronomes do caso reto e oblíquos, pronomes demonstrativos, nomes correferentes etc), catáforas (remetendo para adiante ao invés de retomar o já dito), uso de organizadores textuais, de coesivos etc, e analisar os mecanismos de reformulação e paráfrase utilizados nos textos de divulgação do conhecimento', 'Campo de atuação na vida pública', 'Análise linguística/semiótica', 'Textualização Progressão temática',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (372, 6, 'EF89LP30', true, true, 'Analisar a estrutura de hipertexto e hiperlinks em textos de divulgação científica que circulam na Web e proceder à remissão a conceitos e relações por meio de links', 'Campo de atuação na vida pública', 'Análise linguística/semiótica', 'Textualização',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (373, 6, 'EF89LP31', true, true, 'Analisar e utilizar modalização epistêmica, isto é, modos de indicar uma avaliação sobre o valor de verdade e as condições de verdade de uma proposição, tais como os asseverativos – quando se concorda com (“realmente, evidentemente, naturalmente, efetivamente, claro, certo, lógico, sem dúvida” etc) ou discorda de (“de jeito nenhum, de forma alguma”) uma ideia; e os quase-asseverativos, que indicam que se considera o conteúdo como quase certo (“talvez, assim, possivelmente, provavelmente, eventualmente”)', 'Campo de atuação na vida pública', 'Análise linguística/semiótica', 'Modalização',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (374, 6, 'EF89LP32', true, true, 'Analisar os efeitos de sentido decorrentes do uso de mecanismos de intertextualidade (referências, alusões, retomadas) entre os textos literários, entre esses textos literários e outras manifestações artísticas (cinema, teatro, artes visuais e midiáticas, música), quanto aos temas, personagens, estilos, autores etc, e entre o texto original e paródias, paráfrases, pastiches, trailer honesto, vídeos-minuto, vidding, dentre outros', 'Campo artístico-literário', 'Leitura', 'Relação entre textos',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (375, 6, 'EF89LP33', true, true, 'Ler, de forma autônoma, e compreender – selecionando procedimentos e estratégias de leitura adequados a diferentes objetivos e levando em conta características dos gêneros e suportes – romances, contos contemporâneos, minicontos, fábulas contemporâneas, romances juvenis, biografias romanceadas, novelas, crônicas visuais, narrativas de ficção científica, narrativas de suspense, poemas de forma livre e fixa (como haicai), poema concreto, ciberpoema, dentre outros, expressando avaliação sobre o texto lido e estabelecendo preferências por gêneros, temas, autores', 'Campo artístico-literário', 'Leitura', 'Estratégias de leitura Apreciação e réplica',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (376, 6, 'EF89LP34', true, true, 'Analisar a organização de texto dramático apresentado em teatro, televisão, cinema, identificando e percebendo os sentidos decorrentes dos recursos linguísticos e semióticos que sustentam sua realização como peça teatral, novela, filme etc', 'Campo artístico-literário', 'Leitura', 'Reconstrução da textualidade e compreensão dos efeitos de sentidos provocados pelos usos de recursos linguísticos e multissemióticos',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (377, 6, 'EF89LP35', true, true, 'Criar contos ou crônicas (em especial, líricas), crônicas visuais, minicontos, narrativas de aventura e de ficção científica, dentre outros, com temáticas próprias ao gênero, usando os conhecimentos sobre os constituintes estruturais e recursos expressivos típicos dos gêneros narrativos pretendidos, e, no caso de produção em grupo, ferramentas de escrita colaborativa', 'Campo artístico-literário', 'Produção de textos', 'Construção da textualidade',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (378, 6, 'EF89LP36', true, true, 'Parodiar poemas conhecidos da literatura e criar textos em versos (como poemas concretos, ciberpoemas, haicais, liras, microrroteiros, lambe-lambes e outros tipos de poemas), explorando o uso de recursos sonoros e semânticos (como figuras de linguagem e jogos de palavras) e visuais (como relações entre imagem e texto verbal e distribuição da mancha gráfica), de forma a propiciar diferentes efeitos de sentido', 'Campo artístico-literário', 'Produção de textos', 'Relação entre textos',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (379, 6, 'EF89LP37', true, true, 'Analisar os efeitos de sentido do uso de figuras de linguagem como ironia, eufemismo, antítese, aliteração, assonância, dentre outras', 'Todos os campos de atuação', 'Análise linguística/semiótica', 'Figuras de linguagem',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (380, 6, 'EF09LP01', true, true, 'Analisar o fenômeno da disseminação de notícias falsas nas redes sociais e desenvolver estratégias para reconhecê-las, a partir da verificação/avaliação do veículo, fonte, data e local da publicação, autoria, URL, da análise da formatação, da comparação de diferentes fontes, da consulta a sites de curadoria que atestam a fidedignidade do relato dos fatos e denunciam boatos etc', 'Campo jornalístico/midiático', 'Leitura', 'Reconstrução do contexto de produção, circulação e recepção de textos Caracterização do campo jornalístico e relação entre os gêneros em circulação, mídias e práticas da cultura digital',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (381, 6, 'EF09LP02', true, true, 'Analisar e comentar a cobertura da imprensa sobre fatos de relevância social, comparando diferentes enfoques por meio do uso de ferramentas de curadoria', 'Campo jornalístico/midiático', 'Leitura', 'Relação entre textos',  Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (382, 6, 'EF09LP03', true, true, 'Produzir artigos de opinião, tendo em vista o contexto de produção dado, assumindo posição diante de tema polêmico, argumentando de acordo com a estrutura própria desse tipo de texto e utilizando diferentes tipos de argumentos – de autoridade, comprovação, exemplificação princípio etc', 'Campo jornalístico/midiático', 'Produção de textos', 'Textualização de textos argumentativos e apreciativos',  Null);
