insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (95, 'Valorizar e utilizar os conhecimentos historicamente construídos sobre o mundo físico, social, cultural e digital para entender e explicar a realidade, continuar aprendendo e colaborar para a construção de uma sociedade justa, democrática e inclusiva.', 3, Null, 'C0001', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (96, 'Exercitar a curiosidade intelectual e recorrer à abordagem própria das ciências, incluindo a investigação, a reflexão, a análise crítica, a imaginação e a criatividade, para investigar causas, elaborar e testar hipóteses, formular e resolver problemas e criar soluções (inclusive tecnológicas) com base nos conhecimentos das diferentes áreas.', 3, Null, 'C0002', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (97, 'Valorizar e fruir as diversas manifestações artísticas e culturais, das locais às mundiais, e também participar de práticas diversificadas da produção artístico-cultural.', 3, Null, 'C0003', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (98, 'Utilizar diferentes linguagens – verbal (oral ou visual-motora, como Libras, e escrita), corporal, visual, sonora e digital –, bem como conhecimentos das linguagens artística, matemática e científica, para se expressar e partilhar informações, experiências, ideias e sentimentos em diferentes contextos e produzir sentidos que levem ao entendimento mútuo.', 3, Null, 'C0004', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (99, 'Compreender, utilizar e criar tecnologias digitais de informação e comunicação de forma crítica, significativa, reflexiva e ética nas diversas práticas sociais (incluindo as escolares) para se comunicar, acessar e disseminar informações, produzir conhecimentos, resolver problemas e exercer protagonismo e autoria na vida pessoal e coletiva.', 3, Null, 'C0005', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (100, 'Valorizar a diversidade de saberes e vivências culturais e apropriar-se de conhecimentos e experiências que lhe possibilitem entender as relações próprias do mundo do trabalho e fazer escolhas alinhadas ao exercício da cidadania e ao seu projeto de vida, com liberdade, autonomia, consciência crítica e responsabilidade.', 3, Null, 'C0006', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (101, 'Argumentar com base em fatos, dados e informações confiáveis, para formular, negociar e defender ideias, pontos de vista e decisões comuns que respeitem e promovam os direitos humanos, a consciência socioambiental e o consumo responsável em âmbito local, regional e global, com posicionamento ético em relação ao cuidado de si mesmo, dos outros e do planeta.', 3, Null, 'C0007', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (102, 'Conhecer-se, apreciar-se e cuidar de sua saúde física e emocional, compreendendo-se na diversidade humana e reconhecendo suas emoções e as dos outros, com autocrítica e capacidade para lidar com elas.', 3, Null, 'C0008', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (103, 'Exercitar a empatia, o diálogo, a resolução de conflitos e a cooperação,fazendo-se respeitar e promovendo o respeito ao outro e aos direitos humanos, com acolhimento e valorização da diversidade de indivíduos e de grupos sociais, seus saberes, identidades, culturas e potencialidades, sem preconceitos de qualquer natureza.', 3, Null, 'C0009', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (104, 'Agir pessoal e coletivamente com autonomia, responsabilidade, flexibilidade, resiliência e determinação, tomando decisões com base em princípios éticos, democráticos, inclusivos, sustentáveis e solidários.', 3, Null, 'C0010', true, true, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (105, 'Compreender o funcionamento das diferentes linguagens e práticas culturais (artísticas, corporais e verbais) e mobilizar esses conhecimentos na recepção e produção de discursos nos diferentes campos de atuação social e nas diversas mídias, para ampliar as formas de participação social, o entendimento e as possibilidades de explicação e interpretação crítica da realidade e para continuar aprendendo.', 3, 12, 'C0011', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (106, 'Compreender os processos identitários, conflitos e relações de poder que permeiam as práticas sociais de linguagem, respeitando as diversidades e a pluralidade de ideias e posições, e atuar socialmente com base em princípios e valores assentados na democracia, na igualdade e nos Direitos Humanos, exercitando o autoconhecimento, a empatia, o diálogo, a resolução de conflitos e a cooperação, e combatendo preconceitos de qualquer natureza.', 3, 12, 'C0012', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (107, 'Utilizar diferentes linguagens (artísticas, corporais e verbais) para exercer, com autonomia e colaboração, protagonismo e autoria na vida pessoal e coletiva, de forma crítica, criativa, ética e solidária, defendendo pontos de vista que respeitem o outro e promovam os Direitos Humanos, a consciência socioambiental e o consumo responsável, em âmbito local, regional e global.', 3, 12, 'C0013', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (108, 'Compreender as línguas como fenômeno (geo)político, histórico, cultural, social, variável, heterogêneo e sensível aos contextos de uso, reconhecendo suas variedades e vivenciando-as como formas de expressões identitárias, pessoais e coletivas, bem como agindo no enfrentamento de preconceitos de qualquer natureza.', 3, 12, 'C0014', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (109, 'Compreender  os  processos  de  produção  e  negociação  de  sentidos  nas  práticas  corporais, reconhecendo-as e vivenciando-as como formas de expressão de valores e identidades, em uma perspectiva democrática e de respeito à diversidade.', 3, 12, 'C0015', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (110, 'Apreciar  esteticamente  as  mais  diversas  produções  artísticas  e  culturais,  considerando  suas características  locais,  regionais  e  globais,  e  mobilizar  seus  conhecimentos  sobre  as  linguagens artísticas para dar significado e (re)construir produções autorais individuais e coletivas, exercendo protagonismo de maneira crítica e criativa, com respeito à diversidade de saberes, identidades e culturas.', 3, 12, 'C0016', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (111, 'Mobilizar práticas de linguagem no universo digital, considerando as dimensões técnicas, críticas, criativas, éticas e estéticas, para expandir as formas de produzir sentidos, de engajar-se em práticas autorais e coletivas, e de aprender a aprender nos campos da ciência, cultura, trabalho, informação e vida pessoal e coletiva.', 3, 12, 'C0017', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (112, 'Utilizar estratégias, conceitos e procedimentos matemáticos para interpretar situações em diversos contextos, sejam atividades cotidianas, sejam fatos das Ciências da Natureza e Humanas, das questões socioeconômicas ou tecnológicas, divulgados por diferentes meios, de modo a contribuir para uma formação geral.', 3, 13, 'C0018', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (113, 'Propor ou participar de ações para investigar desafios do mundo contemporâneo e tomar decisões éticas e socialmente responsáveis, com base na análise de problemas sociais, como os voltados a situações  de  saúde,  sustentabilidade,  das  implicações  da  tecnologia  no  mundo  do  trabalho,  entre outros, mobilizando e articulando conceitos, procedimentos e linguagens próprios da Matemática.', 3, 13, 'C0019', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (114, 'Utilizar  estratégias,  conceitos,  definições  e  procedimentos  matemáticos  para  interpretar,  construir modelos e resolver problemas em diversos contextos, analisando a plausibilidade dos resultados e a adequação das soluções propostas, de modo a construir argumentação consistente.', 3, 13, 'C0020', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (115, 'Compreender e utilizar, com flexibilidade e precisão, diferentes registros de representação matemáticos (algébrico,  geométrico,  estatístico,  computacional  etc.),  na  busca  de  solução  e  comunicação  de resultados de problemas.', 3, 13, 'C0021', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (116, 'Investigar e estabelecer conjecturas a respeito de diferentes conceitos e propriedades matemáticas, empregando  estratégias  e  recursos,  como  observação  de  padrões,  experimentações  e  diferentes tecnologias,  identificando  a  necessidade,  ou  não,  de  uma  demonstração  cada  vez  mais  formal  na validação das referidas conjecturas.', 3, 13, 'C0022', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (117, 'Analisar  fenômenos  naturais  e  processos  tecnológicos,  com  base  nas  interações  e  relações  entre matéria e energia, para propor ações individuais e coletivas que aperfeiçoem processos produtivos, minimizem impactos socioambientais e melhorem as condições de vida em âmbito local, regional e global.', 3, 14, 'C0023', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (118, 'Analisar  e  utilizar  interpretações  sobre  a  dinâmica  da  Vida,  da  Terra  e  do  Cosmos  para  elaborar argumentos, realizar previsões sobre o funcionamento e a evolução dos seres vivos e do Universo, e fundamentar e defender decisões éticas e responsáveis.', 3, 14, 'C0024', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (119, 'Investigar  situações-problema  e  avaliar  aplicações  do  conhecimento  científico  e  tecnológico  e  suas implicações no mundo, utilizando procedimentos e linguagens  próprios  das  Ciências  da  Natureza, para propor soluções que considerem demandas locais, regionais e/ou globais, e comunicar suas descobertas e conclusões a públicos variados, em diversos contextos e por meio de diferentes mídias e tecnologias digitais de informação e comunicação (TDIC).', 3, 14, 'C0025', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (120, 'Analisar processos políticos, econômicos, sociais, ambientais e culturais nos âmbitos local, regional, nacional e mundial em diferentes tempos, a partir da pluralidade de procedimentos epistemológicos, científicos e tecnológicos, de modo a compreender e posicionar-se criticamente em relação a eles, considerando diferentes pontos de vista e tomando decisões baseadas em argumentos e fontes de natureza científica.', 3, 15, 'C0026', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (121, 'Analisar a formação de territórios e fronteiras em diferentes tempos e espaços, mediante a compreensão das relações de poder que determinam as territorialidades e o papel geopolítico dos Estados-nações.', 3, 15, 'C0027', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (122, 'Analisar e avaliar criticamente as relações de diferentes grupos, povos e sociedades com a natureza (produção,  distribuição  e  consumo)  e  seus  impactos  econômicos  e  socioambientais,  com  vistas  à proposição  de  alternativas  que  respeitem  e  promovam  a  consciência,  a  ética  socioambiental  e  o consumo responsável em âmbito local, regional, nacional e global.', 3, 15, 'C0028', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (123, 'Analisar as relações de produção, capital e trabalho em diferentes territórios, contextos e culturas, discutindo o papel dessas relações na construção, consolidação e transformação das sociedades.', 3, 15, 'C0029', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (124, 'Identificar e combater as diversas formas de injustiça, preconceito e violência, adotando princípios éticos, democráticos, inclusivos e solidários, e respeitando os Direitos Humanos.', 3, 15, 'C0030', true, false, true);
insert into competencia (id, descricao, id_nivel, id_componente, codigo, bncc, geral, ativo) values (125, 'Participar  do  debate  público  de  forma  crítica,  respeitando  diferentes  posições  e  fazendo  escolhas alinhadas ao exercício da cidadania e ao seu projeto de vida, com liberdade, autonomia, consciência crítica e responsabilidade.', 3, 15, 'C0031', true, false, true);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1402, 15, 'EM13CHS101', true, true, 'Identificar, analisar e comparar diferentes fontes e narrativas expressas em diversas linguagens, com vistas à compreensão de ideias filosóficas e de processos e eventos históricos, geográficos, políticos, econômicos, sociais, ambientais e culturais', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1403, 15, 'EM13CHS102', true, true, 'Identificar, analisar e discutir as circunstâncias históricas, geográficas, políticas, econômicas, sociais, ambientais e culturais de matrizes conceituais (etnocentrismo, racismo, evolução, modernidade, cooperativismo/desenvolvimento etc), avaliando criticamente seu significado histórico e comparando-as a narrativas que contemplem outros agentes e discursos', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1404, 15, 'EM13CHS103', true, true, 'Elaborar hipóteses, selecionar evidências e compor argumentos relativos a processos políticos, econômicos, sociais, ambientais, culturais e epistemológicos, com base na sistematização de dados e informações de diversas naturezas (expressões artísticas, textos filosóficos e sociológicos, documentos históricos e geográficos, gráficos, mapas, tabelas, tradições orais, entre outros)', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1405, 15, 'EM13CHS104', true, true, 'Analisar objetos e vestígios da cultura material e imaterial de modo a identificar conhecimentos, valores, crenças e práticas que caracterizam a identidade e a diversidade cultural de diferentes sociedades inseridas no tempo e no espaço', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1406, 15, 'EM13CHS105', true, true, 'Identificar, contextualizar e criticar tipologias evolutivas (populações nômades e sedentárias, entre outras) e oposições dicotômicas (cidade/campo, cultura/ natureza, civilizados/bárbaros, razão/emoção, material/virtual etc), explicitando suas ambiguidades', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1407, 15, 'EM13CHS106', true, true, 'Utilizar as linguagens cartográfica, gráfica e iconográfica, diferentes gêneros textuais e tecnologias digitais de informação e comunicação de forma crítica, significativa, reflexiva e ética nas diversas práticas sociais, incluindo as escolares, para se comunicar, acessar e difundir informações, produzir conhecimentos, resolver problemas e exercer protagonismo e autoria na vida pessoal e coletiva', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1408, 15, 'EM13CHS201', true, true, 'Analisar e caracterizar as dinâmicas das populações, das mercadorias e do capital nos diversos continentes, com destaque para a mobilidade e a fixação de pessoas, grupos humanos e povos, em função de eventos naturais, políticos, econômicos, sociais, religiosos e culturais, de modo a compreender e posicionar-se criticamente em relação a esses processos e às possíveis relações entre eles', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1409, 15, 'EM13CHS202', true, true, 'Analisar e avaliar os impactos das tecnologias na estruturação e nas dinâmicas de grupos, povos e sociedades contemporâneos (fluxos populacionais, financeiros, de mercadorias, de informações, de valores éticos e culturais etc), bem como suas interferências nas decisões políticas, sociais, ambientais, econômicas e culturais', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1410, 15, 'EM13CHS203', true, true, 'Comparar os significados de território, fronteiras e vazio (espacial, temporal e cultural) em diferentes sociedades, contextualizando e relativizando visões dualistas (civilização/barbárie, nomadismo/sedentarismo, esclarecimento/obscurantismo, cidade/campo, entre outras)', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1411, 15, 'EM13CHS204', true, true, 'Comparar e avaliar os processos de ocupação do espaço e a formação de territórios, territorialidades e fronteiras, identificando o papel de diferentes agentes (como grupos sociais e culturais, impérios, Estados Nacionais e organismos internacionais) e considerando os conflitos populacionais (internos e externos), a diversidade étnico-cultural e as características socioeconômicas, políticas e tecnológicas', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1412, 15, 'EM13CHS205', true, true, 'Analisar a produção de diferentes territorialidades em suas dimensões culturais, econômicas, ambientais, políticas e sociais, no Brasil e no mundo contemporâneo, com destaque para as culturas juvenis', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1413, 15, 'EM13CHS206', true, true, 'Analisar a ocupação humana e a produção do espaço em diferentes tempos, aplicando os princípios de localização, distribuição, ordem, extensão, conexão, arranjos, casualidade, entre outros que contribuem para o raciocínio geográfico', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1414, 15, 'EM13CHS301', true, true, 'Problematizar hábitos e práticas individuais e coletivos de produção, reaproveitamento e descarte de resíduos em metrópoles, áreas urbanas e rurais, e comunidades com diferentes características socioeconômicas, e elaborar e/ou selecionar propostas de ação que promovam a sustentabilidade socioambiental, o combate à poluição sistêmica e o consumo responsável', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1415, 15, 'EM13CHS302', true, true, 'Analisar e avaliar criticamente os impactos econômicos e socioambientais de cadeias produtivas ligadas à exploração de recursos naturais e às atividades agropecuárias em diferentes ambientes e escalas de análise, considerando o modo de vida das populações locais - entre elas as indígenas, quilombolas e demais comunidades tradicionais -, suas práticas agroextrativistas e o compromisso com a sustentabilidade', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1416, 15, 'EM13CHS303', true, true, 'Debater e avaliar o papel da indústria cultural e das culturas de massa no estímulo ao consumismo, seus impactos econômicos e socioambientais, com vistas à percepção crítica das necessidades criadas pelo consumo e à adoção de hábitos sustentáveis', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1417, 15, 'EM13CHS304', true, true, 'Analisar os impactos socioambientais decorrentes de práticas de instituições governamentais, de empresas e de indivíduos, discutindo as origens dessas práticas, selecionando, incorporando e promovendo aquelas que favoreçam a consciência e a ética socioambiental e o consumo responsável', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1418, 15, 'EM13CHS305', true, true, 'Analisar e discutir o papel e as competências legais dos organismos nacionais e internacionais de regulação, controle e fiscalização ambiental e dos acordos internacionais para a promoção e a garantia de práticas ambientais sustentáveis', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1419, 15, 'EM13CHS306', true, true, 'Contextualizar, comparar e avaliar os impactos de diferentes modelos socioeconômicos no uso dos recursos naturais e na promoção da sustentabilidade econômica e socioambiental do planeta (como a adoção dos sistemas da agrobiodiversidade e agroflorestal por diferentes comunidades, entre outros)', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1420, 15, 'EM13CHS401', true, true, 'Identificar e analisar as relações entre sujeitos, grupos, classes sociais e sociedades com culturas distintas diante das transformações técnicas, tecnológicas e informacionais e das novas formas de trabalho ao longo do tempo, em diferentes espaços (urbanos e rurais) e contextos', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1421, 15, 'EM13CHS402', true, true, 'Analisar e comparar indicadores de emprego, trabalho e renda em diferentes espaços, escalas e tempos, associando-os a processos de estratificação e desigualdade socioeconômica', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1422, 15, 'EM13CHS403', true, true, 'Caracterizar e analisar os impactos das transformações tecnológicas nas relações sociais e de trabalho próprias da contemporaneidade, promovendo ações voltadas à superação das desigualdades sociais, da opressão e da violação dos Direitos Humanos', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1423, 15, 'EM13CHS404', true, true, 'Identificar e discutir os múltiplos aspectos do trabalho em diferentes circunstâncias e contextos históricos e/ou geográficos e seus efeitos sobre as gerações, em especial, os jovens, levando em consideração, na atualidade, as transformações técnicas, tecnológicas e informacionais', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1424, 15, 'EM13CHS501', true, true, 'Analisar os fundamentos da ética em diferentes culturas, tempos e espaços, identificando processos que contribuem para a formação de sujeitos éticos que valorizem a liberdade, a cooperação, a autonomia, o empreendedorismo, a convivência democrática e a solidariedade', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1425, 15, 'EM13CHS502', true, true, 'Analisar situações da vida cotidiana, estilos de vida, valores, condutas etc, desnaturalizando e problematizando formas de desigualdade, preconceito, intolerância e discriminação, e identificar ações que promovam os Direitos Humanos, a solidariedade e o respeito às diferenças e às liberdades individuais', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1426, 15, 'EM13CHS503', true, true, 'Identificar diversas formas de violência (física, simbólica, psicológica etc), suas principais vítimas, suas causas sociais, psicológicas e afetivas, seus significados e usos políticos, sociais e culturais, discutindo e avaliando mecanismos para combatê-las, com base em argumentos éticos', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1427, 15, 'EM13CHS504', true, true, 'Analisar e avaliar os impasses ético-políticos decorrentes das transformações culturais, sociais, históricas, científicas e tecnológicas no mundo contemporâneo e seus desdobramentos nas atitudes e nos valores de indivíduos, grupos sociais, sociedades e culturas', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1428, 15, 'EM13CHS601', true, true, 'Identificar e analisar as demandas e os protagonismos políticos, sociais e culturais dos povos indígenas e das populações afrodescendentes (incluindo as quilombolas) no Brasil contemporâneo considerando a história das Américas e o contexto de exclusão e inclusão precária desses grupos na ordem social e econômica atual, promovendo ações para a redução das desigualdades étnico-raciais no país', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1429, 15, 'EM13CHS602', true, true, 'Identificar e caracterizar a presença do paternalismo, do autoritarismo e do populismo na política, na sociedade e nas culturas brasileira e latino-americana, em períodos ditatoriais e democráticos, relacionando-os com as formas de organização e de articulação das sociedades em defesa da autonomia, da liberdade, do diálogo e da promoção da democracia, da cidadania e dos direitos humanos na sociedade atual', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1430, 15, 'EM13CHS603', true, true, 'Analisar a formação de diferentes países, povos e nações e de suas experiências políticas e de exercício da cidadania, aplicando conceitos políticos básicos (Estado, poder, formas, sistemas e regimes de governo, soberania etc)', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1431, 15, 'EM13CHS604', true, true, 'Discutir o papel dos organismos internacionais no contexto mundial, com vistas à elaboração de uma visão crítica sobre seus limites e suas formas de atuação nos países, considerando os aspectos positivos e negativos dessa atuação para as populações locais', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1432, 15, 'EM13CHS605', true, true, 'Analisar os princípios da declaração dos Direitos Humanos, recorrendo às noções de justiça, igualdade e fraternidade, identificar os progressos e entraves à concretização desses direitos nas diversas sociedades contemporâneas e promover ações concretas diante da desigualdade e das violações desses direitos em diferentes espaços de vivência, respeitando a identidade de cada grupo e de cada indivíduo', Null, Null, Null, Null);
insert into habilidade (id, id_componente, codigo, bncc, ativo, descricao, campo_atuacao, pratica_liguagem, objeto_conhecimento, unidade_tematica) values (1433, 15, 'EM13LGG105', true, true, 'Analisar e experimentar diversos processos de remidiação de produções multissemióticas, multimídia e transmídia, desenvolvendo diferentes modos de participação e intervenção social', Null, Null, Null, Null);
